import { GET_TICKETS, ADD_TICKET, CLOSE_TICKET, TICKETS_LOADING } from './types';
import { returnErrors } from './errorActions';
import { tokenConfig } from './authActions';

export const getTickets = (getState) => dispatch => {
    dispatch(setItemsLoading());
    fetch('/tickets')
        .then(res =>
            dispatch({
                type: GET_TICKETS,
                payload: res.data
            })
        )
        .catch(err => 
            dispatch(returnErrors(err.response.data, err.response.status))
            );
};

export const newTicket = () => (dispatch, getState) => {
    fetch('/tickets/create', {
        method: 'POST'
    })
        .then(res => 
            dispatch({
                type: ADD_TICKET,
                payload: res.data
            })
        )
        .catch(err => 
            dispatch(returnErrors(err.response.data, err.response.status))
        );
};

export const closeTicket = id => (dispatch, getState) => {
    fetch(`/tickets/close/${id}`, tokenConfig(getState), {
        method: 'POST'
    })
        .then(res => 
            dispatch({
                type: CLOSE_TICKET,
                payload: id
            })
        )
        .catch(err => 
            dispatch(returnErrors(err.response.data, err.response.status))
        );
};

export const setItemsLoading = () => {
    return {
        type: TICKETS_LOADING
    };
};
