import React, { Component } from 'react';
import LoadingScreen from './loadingscreen';
import axios from 'axios';

export default class ActivatedScreen extends Component {
    state = {
        loading: true
    }

    activate = async () => {
        const hash = window.location.pathname.substring(8)
        await axios
            .get('/api/verify', {
                params: {
                    hash: hash
                }
            })
            .then(res => {
                console.log(res)
            })
        this.state.loading = false;
    }

    componentDidMount() {
        this.activate();
    }
    render() {
        if (this.state.loading === true) {
            return(
            <LoadingScreen />
            )
        }
        return (
            <div>
                Your account has been activated!
            </div>
        )
    }
}
