import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { loginUser } from '../../actions/authActions';
import classnames from 'classnames'
import PropTypes from 'prop-types';
import { Button, Form } from 'react-bootstrap';


class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
          email: '',
          password: '',
          errors: {}
        };
      }

      componentDidMount() {
        // If logged in and user navigates to Login page, should redirect them to dashboard
        if (this.props.auth.isAuthenticated) {
          this.props.history.push("/dashboard");
        }
      }

      componentWillReceiveProps(nextProps) {
        if (nextProps.auth.isAuthenticated) {
          this.props.history.push("/tickets"); // push user to dashboard when they login
        }
        if (nextProps.errors) {
          this.setState({
            errors: nextProps.errors
          });
        }
      }

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
      };
    onSubmit = e => {
        e.preventDefault();
    const userData = {
          email: this.state.email,
          password: this.state.password,
        };
        this.props.loginUser(userData);
      };

    render() {
        const { errors } = this.state;
        return (
            <Form onSubmit={this.onSubmit}>
            <Form.Group controlId="email">
                <Form.Label>Email address</Form.Label>
                <span className='red-text'>{errors.email}{errors.emailnotfound}</span>
                <Form.Control className={classnames('', {invalid: errors.email || errors.emailnotfound })} value={this.state.email} onChange={this.onChange} type='email' error={errors.email} placeholder="Enter email" />
            </Form.Group>
            <Form.Group controlId="password">
                <Form.Label>Password</Form.Label>
                <span className='red-text'>{errors.password}{errors.passwordincorrect}</span>
                <Form.Control className={classnames('', { invalid: errors.password || errors.passwordincorrect })} value={this.state.password} onChange={this.onChange} type='password' error={errors.password} placeholder="Password" />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        )
    }
}

Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});
export default connect(
  mapStateToProps,
  { loginUser }
)(Login);
