import axios from 'axios';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getTicket } from '../actions/ticketActions';
import PropTypes from 'prop-types';
import { Form, Button } from 'react-bootstrap';


class TicketPage extends Component {
    state = {
        title: '',
        description: '',
        customer: {
            name: '',
            contact: ''
        },
        author: {
            id: '',
            name: ''
        },
        errors: {}
    }

    componentDidMount() {
         const { id } = this.props.match.params;
         //this.props.getTicket(id)
         axios
         .get(`/tickets/${id}`)
         .then(res => 
            this.setState(res.data)
         )
         .catch(err =>
            console.log(err)
         );
    }


    onChange = (e) => {
        this.setState({ [e.target.id] : e.value })
    }

    onSubmit = async (e) => {

    }

    render() {
        return (
            <Form>
            <Form.Group controlId="title">
                <Form.Label>Title</Form.Label>
                <Form.Control type="text" value={this.state.title} />
            </Form.Group>
            <Form.Group controlId="description">
                <Form.Label>Description</Form.Label>
                <Form.Control as='textarea' value={this.state.description} onChange={this.onChange} placeholder="Enter ticket description" />
            </Form.Group>
            <Form.Group controlId="name">
                <Form.Label>Customer Name</Form.Label>
                <Form.Control type="text" value={((this.state || {}).customer || {}).name} onChange={this.onChange} />
            </Form.Group>
            <Form.Group controlId="contact">
                <Form.Label>Customer Contact</Form.Label>
                <Form.Control type="text" value={((this.state || {}).customer || {}).contact} onChange={this.onChange} />
            </Form.Group>
            <Form.Group controlId="name">
                <Form.Label>Title</Form.Label>
                <Form.Control type="text" value={((this.state || {}).author || {}).name}  />
            </Form.Group>
            <Button variant="primary" type="submit">Submit</Button>
            </Form>
        )
    }
}

TicketPage.propTypes = {
    getTicket: PropTypes.func.isRequired,
    ticket: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
    ticket: state.ticket
});

export default connect(mapStateToProps, { getTicket })(TicketPage)
