import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getTicket } from '../actions/ticketActions';
import PropTypes from 'prop-types';
import { Form, Button } from 'react-bootstrap';
import { callbackify } from 'util';

class TicketPage extends Component {
    constructor(props) {
    super(props)
    this.state = {
        data: props.ticket.tickets
    }
}

    componentDidMount() {
         const { id } = this.props.match.params;
         this.props.getTicket(id)
    }

    componentDidUpdate(oldProps) {
        // By duplicating the data, you have to then
        // keep the local copy in sync with the
        // updated props...
        if(oldProps.data !== this.props.data) {
          // This triggers an unnecessary re-render
          this.setState({
            data: this.props.ticket.tickets
          });
        }
      }

    onChange = (e) => {
        this.setState({ [e.target.id] : e.target.value })
    }

    render() {
        const details = this.props.ticket.tickets
        console.log(details.title)
        return (
            <Form>
            <Form.Group controlId="title">
                <Form.Label>Title</Form.Label>
                <Form.Control type="text" defaultValue={details.title} onChange={this.onChange} value={this.state.title} placeholder="Enter ticket title" />
            </Form.Group>
            <Form.Group controlId="description">
                <Form.Label>Description</Form.Label>
                <Form.Control as='textarea' onChange={this.onChange} value={this.state.description} placeholder="Enter ticket description" />
            </Form.Group>
            <Button variant="primary" type="submit">Submit</Button>
            </Form>
        )
    }
}

TicketPage.propTypes = {
    getTicket: PropTypes.func.isRequired,
    ticket: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
    ticket: state.ticket
});

export default connect(mapStateToProps, { getTicket })(TicketPage)
