import React, { Component } from 'react';
import axios from 'axios';

export default class ActivatedScreen extends Component {


    activate = async () => {
        const hash = window.location.pathname.substring(8)
        axios
            .get('/api/verify', {
                params: {
                    hash: hash
                }
            })
    }

    componentDidMount() {
        this.activate();
    }
    render() {
        return (
            <div>
                Your account has been activated!
            </div>
        )
    }
}
